mapper.select:
SELECT COUNT(*)
FROM artworks
WHERE uid = ${uid};

SELECT *
FROM not_deleted
WHERE pid BETWEEN #{first} AND #{last};

SELECT COUNT(*)
FROM artworks
WHERE NOT deleted;

mapper.update:
UPDATE artworks
SET deleted = true,
    caption = #{comment}
WHERE pid = ${pid};

UPDATE artworks
SET deleted = true,
    caption = #{comment}
WHERE uid = ${pid};

mapper.select:
SELECT uid, COUNT(*)
FROM not_deleted
GROUP BY uid;

SELECT *
FROM not_deleted
WHERE is_ero
  AND age = 0
  AND sanity_level >= ${level}
  AND total_bookmarks >= ${bookmarks}
ORDER BY RANDOM()
LIMIT ${limit};

SELECT COUNT(*)
FROM artworks
WHERE pid = ${pid};
